#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Author : Mathis PigassouCorrection des Travaux Pratiques :     TP 4 - listes et boucles for"""import random as rd# Exercice 1def somme(liste: list) -> int :    '''    Renvoie la somme des éléments d'une liste d'entiers (sans sum).    '''    somme = 0    for i in liste :        somme += i    return sommedef carres() -> list :    '''    Contient les carrés des 100 premiers entiers.    '''    return [x**2 for x in range(101)]def maximum(liste: list) -> int :    '''    Renvoie le maximum d'une liste d'entiers.    '''    m = liste[0]    for i in liste:        if m < i:            m = i    return m def antecedents() -> list :    '''    100 réels uniformément répartis entre 0 et 3.    '''    return [rd.random()*3 for i in range(1,101)]    def images() -> list :    '''    Contient la liste des images par x →x2/(x3 + 1) des éléments de antecedents.    '''    return [(x**2)/((x**3)+1) for x in antecedents()]# Exercice 2def mon_count(liste: list, x: any) -> int :    '''    Renvoie le nombre d'occurences de x dans la liste "liste".    '''    count = 0    for i in liste :        if i == x :            count += 1    return countdef mon_pop(liste: list, x: any) -> list :    '''    Renvoie la liste liste dans laquelle l'élément d'indice i    a été eﬀacé, sans utiliser pop.    '''    for i in range(len(liste)+1) :        if i == x :             liste.remove(liste[i])    return listedef mon_index(liste: list, x: any) -> int :    '''    renvoie l'indice de la première occurence de x dans liste, sans utiliser index.    '''    for i in range( len(liste)+1) :        if liste[i] == x :            return i    return -1def mon_reverse(liste: list) -> list :    '''    Renvoie la liste inversé sans utiliser reverse.    '''    l =[x for x in liste]    for i in range(len(liste)) :        l[i] = liste[-i-1]    return ldef test() -> None :        # Exercice 1    print(somme(carres()))    print(sum(carres()))        print(maximum(images()))    print(max(images()))    # Exercice 2    test = [14,2,5,7,0,35,2,23,768,66]        print(mon_count(test,2))    print(test.count(2))    print(mon_pop(test, 2))    print(test.pop(2))    print(mon_index(test,35))    print(test.index(35))    print(mon_reverse(test))    print(test[::-1])    print([x for x in reversed(test)])